import axios from "axios";

export const fetchNBANews = async () => {
  try {
    const response = await axios.get(
      "https://nba-latest-news.p.rapidapi.com/articles",
      {
        headers: {
          "X-RapidAPI-Key":
            "c609279aa4msh8fe9e6d486fa636p11b262jsn6b759e501420",
          "X-RapidAPI-Host": "nba-latest-news.p.rapidapi.com",
        },
      },
    );
    console.log(response.data);
    return response.data;
  } catch (error) {
    console.error("Error fetching NBA news:", error);
    return null;
  }
};

const requestInterval = 60000;

export const startFetchingDataWithInterval = () => {
  const intervalId = setInterval(async () => {
    await fetchNBANews();
  }, requestInterval);

  return intervalId;
};
