// PlayerInfo.js
import React from "react";
import FlagContainer from "../FlagContainer/FlagContainer";
import "./PlayerInfo.css";

const PlayerInfo = ({ player }) => {
  return (
    <div className="player-info">
      <div className="player-info-image-container">
        <img
          src={player.image}
          alt={`${player.firstName} ${player.lastName}`}
          className="player-image"
        />
      </div>
      <div className="player-info-name-container">
        <h2>
          {player.firstName} {player.lastName}
        </h2>
        <h2>{player.currentIndex} GC</h2>
      </div>
      <div className="player-info-team-container">
        <FlagContainer
          flagSrc="https://upload.wikimedia.org/wikipedia/en/b/be/Flag_of_England.svg"
          text="England"
        />
        <FlagContainer
          flagSrc="https://upload.wikimedia.org/wikipedia/en/7/7a/Manchester_United_FC_crest.svg"
          text={player.team}
        />
      </div>
      <div className="player-details">
        <div className={"player-details-position"}>
          <p>Position: </p>
          {player.currentIndex}
        </div>
        <p>Match played: {player.matchPlayed}</p>
        <p>Goals: {player.goals}</p>
      </div>
      <button className="add-button">Add to my team</button>
    </div>
  );
};

export default PlayerInfo;
