import React, { useEffect, useState } from "react";
import nba_homepage from "../../image/image-nba-homepage.jpeg";
import "./HomePage.css";

function HomePage(props) {
  const [nbaNews, setNBANews] = useState([]);

  useEffect(() => {
    const fetchNBANews = async () => {
      try {
        const response = await fetch("/nba/nba-news");
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setNBANews(data);
      } catch (error) {
        console.error("Error fetching NBA news:", error);
      }
    };

    fetchNBANews();
  }, []);

  return (
    <div className="homepage-content">
      <div className="homepage-image-section">
        <img src={nba_homepage} alt="nba-homepage" />
        <div className="homepage-image-text-overlay">
          <h2>The world's First</h2>
          <p>Additional description or information</p>
        </div>
      </div>
      <div className="homepage-nba-news">
        <h3>NBA News</h3>
        <ul>
          {nbaNews.map((newsItem, index) => (
            <li key={index}>
              <a href={newsItem.url} target="_blank" rel="noopener noreferrer">
                {newsItem.title}
              </a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default HomePage;
