import React, { useState } from "react";
import "./SearchBox.css";

const SearchBox = () => {
  const [isActive, setIsActive] = useState(false);

  const handleClick = () => {
    setIsActive(true);
  };

  const handleBlur = () => {
    setIsActive(false);
  };

  return (
    <div
      className={`search-box ${isActive ? "active" : ""}`}
      onClick={handleClick}
    >
      {!isActive && <i className="search-icon">üîç</i>}
      {isActive && (
        <input
          type="text"
          className="search-input"
          onBlur={handleBlur}
          autoFocus
        />
      )}
    </div>
  );
};

export default SearchBox;
